import React, { useState, useEffect } from "react";
import FormField from "./FormField";
import SectionButton from "./SectionButton";
import { useAuth } from "./../util/auth.js";
import { useForm } from "react-hook-form";
import { getResponses } from "./../util/sheets.js";
import { redirectToCheckout } from "./../util/stripe.js";

function DashboardSms(props) {
  const auth = useAuth();
  const [pending, setPending] = useState(false);
  const [responses, setResponses] = useState([
      ["Rylie", 2624243872, ["Pepperoni"], ["Blue", "Red"]],
      ["Jordan", 6309010523, ["Pepperoni", "Sausage"], ["Blue"]],
  ]);
  const [questions, setQuestions] = useState(["Name", "Number", "Pizza Topping", "Favorite Color"]);
  const [formAlert, setFormAlert] = useState(null);

  useEffect(() => {
      if (auth.user.planIsActive && auth.user.sheetLink) {
        setPending(true);
        getResponses(auth.user.sheetLink)
        .then((resp) => {
          setResponses(resp.customers);
          setQuestions(resp.questions);
          setPending(false);
        })
        .catch((e) => {
          console.error('Error: ', e);
          setPending(false);
        });
      }
      else {
        // Alert that no subscription and should sign up
        // Redirect to checkout?
        console.log(auth.user);
      }
    }, [auth.user]);

  console.log(questions);

  let numbers = [2624243872, 6309010523];

  const { register, handleSubmit, errors, reset } = useForm();

  const onSubmit = (data) => {
    console.log(data);
    // Show pending indicator
    setPending(true);

    // Check Stripe account for if paid
    // If paid generate charge and ask if successful
    // Then check format of message data
    // Then send message
    // Then ask if successful
    auth
      .updatePassword(data.pass)
      .then(() => {
        // Clear form
        reset();
        // Set success status
        props.onStatus({
          type: "success",
          message: "Your password has been updated",
        });
      })
      // Maybe also catch error for needing a subscription?
      .catch((error) => {
        if (error.code === "auth/requires-recent-login") {
          // Update state to show re-authentication modal
          props.onStatus({
            type: "requires-recent-login",
            // Resubmit after reauth flow
            callback: () => onSubmit({ pass: data.pass }),
          });
        } else {
          // Set error status
          props.onStatus({
            type: "error",
            message: error.message,
          });
        }
      })
      .finally(() => {
        // Hide pending indicator
        setPending(false);
      });
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      {!pending && questions.map((question) => (
          <FormField
          key={question}
          name={question}
          type="checkbox"
          label="Check me"
          error={errors.question}
          inputRef={register({
            required:
              "Please answer this question.",
          })}
        />
      )
      )}

      <FormField
        name="message"
        type="textarea"
        label="Please enter your SMS"
        placeholder="Write to your people"
        error={errors.message}
        inputRef={register({
          required: "Please enter your text message to send!",
        })}
      />
      <div className="field">
        <div className="control">
          <SectionButton
            parentColor={props.parentColor}
            size="medium"
            state={pending ? "loading" : "normal"}
          >
            Send
          </SectionButton>
        </div>
      </div>
    </form>
  );
}

export default DashboardSms;
