import { GoogleSpreadsheet } from "google-spreadsheet";

async function getSheet(sheetID, pageID) {
    const doc = new GoogleSpreadsheet(sheetID, pageID);

    const client_email = process.env.REACT_APP_SHEETS_CLIENT_EMAIL;
    const private_key = process.env.REACT_APP_SHEETS_PRIVATE_KEY;

    try {
        await doc.useServiceAccountAuth({
            client_email: client_email,
            private_key: private_key,
        });
        await doc.loadInfo();
        const sheet = doc.sheetsById[pageID];
        return sheet;
    } catch (e) {
        console.error('Error: ', e);
    }
}

// Fetch a Google Sheet
export function getResponses(sheetLink) {
