import { GoogleSpreadsheet } from "google-spreadsheet";

async function getSheet(sheetID, pageID) {
    const doc = new GoogleSpreadsheet(sheetID, pageID);
    const client_email = process.env.REACT_APP_SHEETS_CLIENT_EMAIL;
    const private_key = process.env.REACT_APP_SHEETS_PRIVATE_KEY;

    try {
        await doc.useApiKey(process.env.REACT_APP_SHEETS_API_KEY);
        await doc.loadInfo();
        const sheet = doc.sheetsById[pageID];
        return sheet;
    } catch (e) {
        console.error('Error: ', e);
    }
}

// Fetch a Google Sheet
export async function getResponses(sheetLink) {
    const sheetID = sheetLink.slice(sheetLink.lastIndexOf("/d/") + 3, sheetLink.lastIndexOf('/edit#'));
    const pageID = sheetLink.slice(sheetLink.lastIndexOf("gid=") + 4, sheetLink.length);

    const doc = new GoogleSpreadsheet(sheetID, pageID);
    const client_email = process.env.REACT_APP_SHEETS_CLIENT_EMAIL;
    const private_key = process.env.REACT_APP_SHEETS_PRIVATE_KEY;

    try {
        await doc.useApiKey(process.env.REACT_APP_SHEETS_API_KEY);
        await doc.loadInfo();
        const sheet = doc.sheetsById[pageID];
        const questions = sheet.headerValues;
        console.log(sheet);
        console.log(sheet.headerValues);
        const rows = await sheet.getRows();
        const customers = rows.map((r) => {
            return r._rawData;
        });

        console.log(questions);
        console.log(customers);
        return customers;
    } catch (e) {
        console.error('Error: ', e);
    }
  }
