import { GoogleSpreadsheet } from "google-spreadsheet";

export function filterNumbers(data, customers, phoneCol) {
    let numbers = [];
    let filters = Object.entries(data).filter(([key, value]) => value);
    filters.forEach((f) => {
      const [question, value] = f[0].split('|||');
      // Adds numbers by whether answer is or includes checked response (may need to edit)
      console.log(customers);
      console.log(question);
      for(const i=0; i < customers.question.options.length; i++) {
          if (customers.question.options[i] === value) {
              numbers.push(customers.phoneCol.options[i]);
          } else if (customers.question.options[i].indexOf(value) >= 0) {
              numbers.push(customers.phoneCol.options[i]);
          }
      }
    });
    numbers = [...new Set(numbers)];
    console.log(numbers);
    return numbers;
}

function formatResponses(questions, responses) {
    let obj = {};
    for (let i = 0; i < questions.length; i++) {
        let q = questions[i];
        let all = [];
        let a = responses.map((r) => {
            if (r[i] && r[i].trim()) {
                all.push(...r[i].split(','));
            }
            return r[i];
        });
        all = [...new Set(all)];
        console.log(all);
        if (all.length > 0) {
            obj[q] = {
                responses: a,
                options: all
            };
        }
    }
    return obj;
}

// Fetch a Google Sheet
export async function getResponses(sheetLink) {
    const sheetID = sheetLink.slice(sheetLink.lastIndexOf("/d/") + 3, sheetLink.lastIndexOf('/edit#'));
    const pageID = sheetLink.slice(sheetLink.lastIndexOf("gid=") + 4, sheetLink.length);

    const doc = new GoogleSpreadsheet(sheetID, pageID);

    try {
        await doc.useApiKey(process.env.REACT_APP_SHEETS_API_KEY);
        await doc.loadInfo();
        let sheet = doc.sheetsById[pageID];
        let rows = await sheet.getRows();
        let responses = rows.map(r => r._rawData);
        await sheet.loadHeaderRow();
        let questions = sheet.headerValues;
        return formatResponses(questions, responses);
    } catch (e) {
        console.error('Error: ', e);
    }
  }
