import { GoogleSpreadsheet } from "google-spreadsheet";

// Fetch a Google Sheet
export async function getSheet(sheetID) {
    const doc = new GoogleSpreadsheet(sheetID);
    console.log(doc);
    console.log(process.env.REACT_APP_SHEETS_CLIENT_EMAIL);
    console.log(process.env.REACT_APP_SHEETS_PRIVATE_KEY.replace(/\\n/g, "\n"));
    await doc.useServiceAccountAuth({
      client_email: process.env.REACT_APP_SHEETS_CLIENT_EMAIL,
      private_key: process.env.REACT_APP_SHEETS_PRIVATE_KEY.replace(/\\n/g, "\n")
    });
    await doc.loadInfo();
    console.log(doc.title);

    const sheet = doc.sheetsByIndex[0];
    console.log(sheet.title);
  }
