import { apiRequest } from "./util";
import { useAuth } from "./../util/auth.js";

export async function sendSms(number, message) {
    // Create a twilio session
    return await apiRequest("twilio-send-sms", "POST", {
        to: String(number),  // Need someway to enforce number format
        message: message
    });
}

export function batchSms(numbers, message) {
    const auth = useAuth();
    let requests = [];

    // Text request
    const requestSms = numbers.forEach(async (num) => {
        await sendSms(num, message)
        .then((status) => {
            messages.push({
                to: status.to,
                from: status.from,
                body: status.body,
                dateSent: status.dateSent,
                errorMessage: status.errorMessage,
                price: status.price
            });
            console.log(status);
        })
        .catch((error) => {
            alert(error.message);
        });
    });
    Promise.all(requests).then(() => {
        messages = auth.user.messages ? [...messages, ...auth.user.messages] : messages;
        auth.updateProfile({messages: messages});
        console.log('messages');
        console.log(auth.user.messages);
    });
}
